# Create table in region us-west-1 from DynamoDB console : SatishTestTable
# Use only a hash number : primarykey
# With capacities read : 20 ; write 10

# From your mac terminal using AWS CLI, fill up the table
for serialno in {1..2000}
do
aws dynamodb put-item --table-name SatishTestTable --region us-west-1 --item '{ "primarykey" : {"N":"'$serialno'"}, "text1" : {"S": ""}}'
done&

# Read from the DynamoDB table
for i in {1..1000}
do
  aws dynamodb scan --table-name SatishTestTable --region us-west-1 > /dev/null
  aws dynamodb get-item --table-name SatishTestTable --key '{ "primarykey" : {"N" : "'$((RANDOM%2000))'"} }' --region us-west-1 >/dev/null
  sleep 1
done&

# Run the above READ for loop as many times as you want to generate more load
# Usually 5 copies is okay on a single machine to avoid network overload